sort(poi2,function(a,b) return a.X<b.X end)
rad1=rad1/Nx.Map:GWZS(self.MaI)/4.575
local raS=rad1 ^ 2
local stC1=#poi2
local mer=true
while mer do
mer=false
local clo1=999999999
local cI1
local cI2
for n1,pt1 in ipairs(poi2) do
for n2=n1+1,#poi2 do
local pt2=poi2[n2]
if pt2.X-pt1.X>rad1 then
break
end
local d=(pt1.X-pt2.X) ^ 2+((pt1.Y-pt2.Y)/1.5) ^ 2
if d<clo1 then
clo1=d
cI1=n1
cI2=n2
end
end
end
if clo1 ^ .5<rad1 then
local pt1=poi2[cI1]
local pt2=poi2[cI2]
pt1.X=(pt1.X+pt2.X)*.5
pt1.Y=(pt1.Y+pt2.Y)*.5
tremove(poi2,cI2)
mer=true
sort(poi2,function(a,b) return a.X<b.X end)
end
end
Nx.prt("Merged %s in %.1f secs",stC1-#poi2,GetTime()-tm)
end
