function Nx.Map:MZT(con1,zon,frm1,alp,lev)
local zna,zx,zy,zw,zh=self:GWZI(con1,zon)
if not zx then
return
end
local sca=self.ScD
local clW=self.MaW
local clH=self.MaH
local x=(zx-self.MPXD)*sca+clW/2
local y=(zy-self.MPYD)*sca+clH/2
local bx=0
local by=0
local bw=zw*1024/1002/4*sca
local bh=zh*768/668/3*sca
local w,h
local tX1,tX2
local tY1,tY2
for i=1,NUM_WORLDMAP_DETAIL_TILES do
local frm=frm1[i]
if frm then
tX1=0
tX2=1
tY1=0
tY2=1
local vx0=bx*bw+x
local vx1=vx0
local vx2=vx0+bw
if vx1<0 then
vx1=0
tX1=(vx1-vx0)/bw
end
if vx2>clW then
vx2=clW
tX2=(vx2-vx0)/bw
end
local vy0=by*bh+y
local vy1=vy0
local vy2=vy0+bh
if vy1<0 then
vy1=0
tY1=(vy1-vy0)/bh
end
if vy2>clH then
vy2=clH
tY2=(vy2-vy0)/bh
end
w=vx2-vx1
h=vy2-vy1
if w<=0 or h<=0 then
frm:Hide()
else
frm:SetPoint("TOPLEFT",vx1,-vy1-self.TiH)
frm:SetWidth(w)
frm:SetHeight(h)
frm:SetFrameLevel(lev)
frm.tex1:SetTexCoord(tX1,tX2,tY1,tY2)
frm.tex1:SetVertexColor(1,1,1,alp)
frm:Show()
end
end
bx=bx+1
if bx>=4 then
bx=0
by=by+1
end
end
end
